import React, { useState } from 'react';
import './css/ToggleMenu.css';

const ToggleMenu = () => {
    const [openFirst, setOpenFirst] = useState(false);
    const [openSecond, setOpenSecond] = useState(false);
    const [openThird, setOpenThird] = useState(false);

    return (
        <div className="menu-container">
            <button onClick={() => setOpenFirst(!openFirst)}>Toggle Menu 1</button>

            <div className={`menu first-menu ${openFirst ? 'open' : ''}`}>
                <button onClick={() => setOpenSecond(!openSecond)}>Toggle Menu 2</button>

                <div className={`menu second-menu ${openSecond ? 'open' : ''}`}>
                    <button onClick={() => setOpenThird(!openThird)}>Toggle Menu 3</button>

                    <div className={`menu third-menu ${openThird ? 'open' : ''}`}>
                        <p>Menu item 3.1</p>
                        <p>Menu item 3.2</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ToggleMenu;
