import React, { useState } from "react";
import "./css/ToggleMenu.css";

const ToggleMenu = () => {
  const [openFirst, setOpenFirst] = useState(false);
  const [openSecond, setOpenSecond] = useState(false);
  const [openThird, setOpenThird] = useState(false);

  return (
    <div className="menu-container">
      <button onClick={() => setOpenFirst(!openFirst)}>Toggle Menu 1</button>

      {openFirst && (
        <div className="menu first-menu">
          <button onClick={() => setOpenSecond(!openSecond)}>
            Toggle Menu 2
          </button>

          {openSecond && (
            <div className="menu second-menu">
              <button onClick={() => setOpenThird(!openThird)}>
                Toggle Menu 3
              </button>

              {openThird && (
                <div className="menu third-menu">
                  <p>Menu item 3.1</p>
                  <p>Menu item 3.2</p>
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ToggleMenu;
