import React, { useState } from "react";
import "../css/ToggleMenu.css";
import DensityMediumIcon from "@mui/icons-material/DensityMedium";

const ToggleMenu = () => {
  const [isOpen, setIsopen] = useState(false);
  const [isSecondOpen, setIsSecondOpen] = useState(false);

  const [categories, setCategories] = useState([
    "패션의류/잡화",
    "뷰티",
    "출산/유아동",
    "식품",
    "주방용품",
    "생활용품",
    "홈인테리어",
    "가전디지털",
    "스포츠레저",
    "자동차용품",
    "헬스/건강",
  ]);

  const ToggleSidebar = () => {
    isOpen === true ? setIsopen(false) : setIsopen(true);
  };

  const ToggleSecondSidebar = () => {
    isSecondOpen === true ? setIsSecondOpen(false) : setIsSecondOpen(true);
  };

  return (
    <div style={{ float: "left" }}>
      <div className="btn btn-primary" onClick={ToggleSidebar}>
        <DensityMediumIcon />
      </div>
      <div className={`sidebar ${isOpen == true ? "active" : ""}`}>
        <div className="sd-header">
          <h4 className="mb-0">카테고리</h4>
        </div>
        <div className="sd-body">
          <ul>
            {categories.map((category, index) => (
              <li
                key={index}
                onClick={
                  category === "패션의류/잡화" ? ToggleSecondSidebar : null
                }
              >
                <a className="sd-link">{category}</a>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div
        className={`sidebar-overlay ${isOpen == true ? "active" : ""}`}
        onClick={ToggleSidebar}
      ></div>

      <div className={`second-sidebar ${isSecondOpen == true ? "active" : ""}`}>
        <div className="sd-header"></div>
        <div className="sd-body">
          <ul>
            <li>
              <a className="sd-link">남성의류</a>
            </li>
            <li>
              <a className="sd-link">여성의류</a>
            </li>
            <li>
              <a className="sd-link">공용</a>
            </li>
            <li>
              <a className="sd-link">유아동</a>
            </li>
          </ul>
        </div>
      </div>
      <div
        className={`sidebar-overlay ${isSecondOpen == true ? "active" : ""}`}
        onClick={() => {
          ToggleSidebar();
          ToggleSecondSidebar();
        }}
      ></div>
    </div>
  );
};

export default ToggleMenu;
